def findAlphaBeta(COMPANY_NAME):
    tickerNifty = '%5ENSEI'
    tickerCompany = f'{COMPANY_NAME}.NS'

    NIFTY_CSV = '/Users/boi_akay/Desktop/Stocks/data/NSEI.csv'
    NIFTY_NAME = 'NIFTY'

    COMPANY_CSV = f'/Users/boi_akay/Desktop/Stocks/data/{COMPANY_NAME}.csv'

    MERGED_FILE_PATH = '/Users/boi_akay/Desktop/Stocks/data/merged_file.csv'
    FINAL_DATA = '/Users/boi_akay/Desktop/Stocks/STOCKS_DATA_AKAY.CSV'


    # check for existing row
    tempDF = pd.read_csv(FINAL_DATA)
    if COMPANY_NAME in tempDF['Company_Name'].values:
        print(Akaycolours.RED+f'{COMPANY_NAME} is already present'+Akaycolours.RESET)
        tempDF = tempDF[tempDF['Company_Name'] != COMPANY_NAME]
        tempDF.to_csv(FINAL_DATA,index=False)

    sucess = downloadStockData(tickerNifty,NIFTY_CSV)
    sucess = downloadStockData(tickerCompany,COMPANY_CSV)
    if(sucess==0):
        return COMPANY_NAME
    sucess = mergeFiles(NIFTY_CSV,COMPANY_CSV,MERGED_FILE_PATH,COMPANY_NAME,NIFTY_NAME)
    if(sucess==0):
        return COMPANY_NAME

    merged_df = pd.read_csv(MERGED_FILE_PATH)

    std_dev_company = merged_df[COMPANY_NAME+'_change'].std()
    print(f'{COMPANY_NAME} standard deviation = {std_dev_company}')
    std_dev_nifty = merged_df[NIFTY_NAME+'_change'].std()
    print(f'{NIFTY_NAME} standard deviation = {std_dev_nifty}')
    
    correlation = merged_df[COMPANY_NAME+'_change'].corr(merged_df[NIFTY_NAME+'_change'])

    beta = (correlation*std_dev_company)/std_dev_nifty

    # finding last updated price of stock
    lastPrice = realTimeStockData(COMPANY_NAME)
    # finding forcast 

    avgForcast  = getForcastData(COMPANY_NAME)



    print(Akaycolours.RED+Akaycolours.BOLD+f'{COMPANY_NAME} Beta        = {beta}'+Akaycolours.RESET)
    print(Akaycolours.RED+Akaycolours.BOLD+f'{COMPANY_NAME} avgForcast  = {avgForcast}'+Akaycolours.RESET)
    print(Akaycolours.RED+Akaycolours.BOLD+f'{COMPANY_NAME} lastPrice   = {lastPrice}'+Akaycolours.RESET)

    start_date = merged_df.iloc[0]['Date']
    last_date = merged_df.iloc[-1]['Date']
    # Open the file in append mode ('a') to add data without overwriting
    with open(FINAL_DATA, 'a', newline='') as csvfile:
        writer = csv.writer(csvfile)
        writer.writerow([COMPANY_NAME, start_date, last_date,lastPrice, std_dev_company ,beta,avgForcast])




    df = pd.read_csv(FINAL_DATA)
    df = df.sort_values(by='Beta')
    df.to_csv(FINAL_DATA,index=False)

    if os.path.exists(MERGED_FILE_PATH):
        os.remove(MERGED_FILE_PATH)
    if os.path.exists(COMPANY_CSV):
        os.remove(COMPANY_CSV)
    if os.path.exists(NIFTY_CSV):
        os.remove(NIFTY_CSV)
    return 'NO_TROUBLE_FOUND'


